    const showPrize = gameState.phase === "result" || isRevealed;

    // Door background
    let fillColor = "#8B4513"; // Brown door
    if (isRevealed) {
      fillColor = "#d3d3d3"; // Gray for opened
    } else if (gameState.phase === "result") {
