        fillColor = "#FFD700"; // Gold for prize
      } else {
        fillColor = "#d3d3d3";
      }
    }

    // Door rectangle
    svg.append("rect")
      .attr("x", x)
      .attr("y", y)
      .attr("width", doorWidth)
